import React from 'react'
import CustomButton from '../custom-button/custom-button.component'
import './card-dropdown.style.scss';
import CartItem from '../cart-item/cart-item.component';
import { connect } from 'react-redux';
import {withRouter} from 'react-router-dom';
import {selectCartItemCount,selectCartItem} from '../../redux/cart/cart.selector';



const CardDropDown = ({ cartItem,history }) => {
    return (
        <div className='cart-dropdown'>
            <div className='cart-items'>

                {

                    cartItem.lenght ?(
                    cartItem.map(cartItems => (
                         <CartItem key={cartItems.id} item={cartItems} />
                    ))

                        ): (<span className='empty-message'>Your cart is empty</span>

                        )
                }

            </div>

            <CustomButton onClick={()=> history.push()}> Go To Checkout</CustomButton>


        </div>
    )
}

const mapStateToProps = (state) => ({
    cartItem : selectCartItem(state)
    

})


export default withRouter(connect(mapStateToProps)(CardDropDown));
