import styled, { css } from 'styled-components';


const ButtonStyle = css`
background-color: black;
color: white;
border: none;
&:hover {
    background-color: black;
    color: white;
    border: none;
  }


`


const InvertedButtonStyles = css`

background-color: white;
color: black;
border: 1px solid black;

&:hover {
  background-color: black;
  color: white;
  border: none;
}
}

`
const GoggleSigninStyles = css`

background-color: #4275f4;
color: white;
}
&:hover {
background-color: #358fe8;
border: none;
}


`

const getButtonStyle = props => {
    if (props.GoggleSigninStyles) {
        return GoggleSigninStyles
    }
    return props.InvertedButtonStyles ? InvertedButtonStyles : ButtonStyle;

}


export const coustomButtonContainer = styled.button`

min-width: 165px;
width: auto;
height: 50px;
letter-spacing: 0.5px;
line-height: 50px;
padding: 0 35px 0 35px;
font-size: 15px;

text-transform: uppercase;
font-family: "Open Sans Condensed";
font-weight: bolder;
border: none;
display: flex;
cursor: pointer;
justify-content: center;
&:hover {
  background-color: white;
  color: black;
  border: 1px solid black;
}

&.google-sign-in {
  background-color: #4275f4;
  color: white;
}
&:hover {
  background-color: #358fe8;
  border: none;
}

&.inverted {
  background-color: white;
  color: black;
  border: 1px solid black;

  
}
}

${getButtonStyle}


`
