import React from 'react';
import './collection.style.scss';
import CollectionOverview from '../../components/collection-overview/CollectionOverview';
import { connect } from 'react-redux';
import { selectCollection } from '../../redux/shop/shop.selector';
import CollectionItem from '../../components/collection-item/collection-item.compoenet';
import CollectionContext from '../../context/collection/collection.context';

const collectionPage = ({ math }) => {

    return(
    <CollectionContext.Consumer>
        {
            collections=>{
                const collection=collections[match.prams.collectionId]
                const {title,item}=collection
                return(
                    <div className='collection-page'>
                    <h2 className='title'>{title}</h2>
                    <div className='items'>
                        {
                            items.map(item => (
                                <CollectionItem key={item.id} item={item} />
                            ))
                        }
                    </div>
        
        
                </div>
        
                )

            }


        }

    
       
    </CollectionContext.Consumer>
    
    )
}

const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.prams.collectionId)(state)

})


export default connect(mapStateToProps)(collectionPage);
