import React,{useContext} from 'react';
import './collection.style.scss';

import CollectionItem from '../../components/collection-item/collection-item.compoenet';
import CollectionContext from '../../context/collection/collection.context';

const collectionPage = ({ math}) => {
    
    const collections = useContext(CollectionContext);
    const collection = collections[math.prams.collectionId]
    const { title, items } = collection;

    return (
        <div className='collection-page'>
            <h2 className='title'>{title}</h2>
            <div className='items'>  
            {
                items.map(item=>(
                    <CollectionItem key={item.id} item={item}/>
                ))
            }
            </div>


        </div>
    )
}




export default collectionPage;
