import React from 'react';
import { connect } from 'react-redux';
import { toggleCartHidden } from '../../redux/cart/cart.action';
import { ReactComponent as ShoppingIcon } from '../../assest/1.svg'

import './card-icon.style.scss';

const CardIcon = ({ toggleCartHidden,itemCount }) => (

    <div className='cart-icon'>
        <ShoppingIcon className='shopping-icon' onClick={toggleCartHidden} />
<span className='item-count'>{itemCount}</span>

    </div>

)

const mapDispatchToProps = dispatch => ({

    toggleCartHidden: () => dispatch(toggleCartHidden())

})


const mapStateToProps = ({ cart: { cartItem } }) => {

console.log('im being caled');

return { itemCount : cartItem.reduce(
    (accumulatetdQuantity,cartItem)=>accumulatetdQuantity + cartItem.quantity,0
)
}
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(CardIcon);
